{
  "query": "query { getContestList(includeGym: false) { id name type phase frozen durationSeconds startTimeSeconds } }"
}

{
  "query": "query { getContestList(includeGym: true) { id name type phase frozen durationSeconds startTimeSeconds } }"
}

{
  "query": "query { getContestStandings(contestId: {{contest_id}}, showUnofficial: false) { contest { id name type phase } problems { index name points tags } rows { rank points penalty successfulHackCount unsuccessfulHackCount party { members { handle } participantType } } } }"
}

{
  "query": "query { getContestStandings(contestId: {{contest_id}}, showUnofficial: true) { contest { id name type phase } problems { index name points tags } rows { rank points penalty successfulHackCount unsuccessfulHackCount party { members { handle } participantType } } } }"
}

{
  "query": "query { getContestSubmissions(contestId: {{contest_id}}) { id contestId creationTimeSeconds problem { index name points } author { members { handle } } programmingLanguage verdict passedTestCount timeConsumedMillis memoryConsumedBytes } }"
}

{
  "query": "query { getContestSubmissions(contestId: {{contest_id}}, handle: \"{{user_handle}}\") { id contestId creationTimeSeconds problem { index name points } author { members { handle } } programmingLanguage verdict passedTestCount timeConsumedMillis memoryConsumedBytes } }"
}

{
  "query": "query { getContestRatingChanges(contestId: {{contest_id}}) { contestId contestName handle rank ratingUpdateTimeSeconds oldRating newRating } }"
}

{
  "query": "query { getContestHacks(contestId: {{contest_id}}) { id creationTimeSeconds hacker { members { handle } } defender { members { handle } } verdict problem { index name } } }"
}

{
  "query": "query { getCompleteContestData(contestId: {{contest_id}}, showUnofficial: false) { contest { id name type phase } problems { index name points tags } standings { rank points penalty party { members { handle } } } submissions { id verdict problem { index } author { members { handle } } } ratingChanges { handle rank oldRating newRating } hacks { id verdict hacker { members { handle } } defender { members { handle } } } } }"
}

{
  "query": "query { getCompleteContestData(contestId: {{contest_id}}, showUnofficial: true) { contest { id name type phase } problems { index name points tags } standings { rank points penalty party { members { handle } } } submissions { id verdict problem { index } author { members { handle } } } ratingChanges { handle rank oldRating newRating } hacks { id verdict hacker { members { handle } } defender { members { handle } } } } }"
}
