graphql.query:
query GetContestList($includeGym: Boolean) {
  contestList(includeGym: $includeGym) {
    id
    name
    type
    phase
    frozen
    durationSeconds
    startTimeSeconds
    relativeTimeSeconds
  }
}

query GetContestProblems($contestId: Int!) {
  contestProblems(contestId: $contestId) {
    contestId
    index
    name
    type
    points
    rating
    tags
  }
}

query GetStandings($contestId: Int!, $from: Int, $count: Int, $showUnofficial: Boolean) {
  contestStandings(contestId: $contestId, from: $from, count: $count, showUnofficial: $showUnofficial) {
    contest {
      id
      name
      type
      phase
      frozen
      durationSeconds
      startTimeSeconds
      relativeTimeSeconds
    }
    problems {
      contestId
      index
      name
      type
      points
      rating
      tags
    }
    rows {
      rank
      points
      penalty
      successfulHackCount
      unsuccessfulHackCount
      party {
        contestId
        members {
          handle
          name
        }
        participantType
        ghost
        room
        startTimeSeconds
      }
      problemResults {
        points
        rejectedAttemptCount
        type
        bestSubmissionTimeSeconds
      }
    }
  }
}

query GetTopStandings($contestId: Int!) {
  contestStandings(contestId: $contestId, from: 1, count: 10) {
    contest {
      name
    }
    rows {
      rank
      points
      penalty
      party {
        members {
          handle
        }
      }
    }
  }
}

query GetSubmissions($contestId: Int!, $count: Int, $handle: String) {
  contestSubmissions(contestId: $contestId, count: $count, handle: $handle) {
    submissions {
      id
      contestId
      creationTimeSeconds
      relativeTimeSeconds
      problem {
        contestId
        index
        name
        type
        points
        rating
        tags
      }
      author {
        contestId
        members {
          handle
          name
        }
        participantType
        ghost
        room
        startTimeSeconds
      }
      programmingLanguage
      verdict
      testset
      passedTestCount
      timeConsumedMillis
      memoryConsumedBytes
    }
  }
}

query GetUserSubmissions($contestId: Int!, $handle: String!) {
  contestSubmissions(contestId: $contestId, handle: $handle) {
    submissions {
      id
      creationTimeSeconds
      relativeTimeSeconds
      problem {
        index
        name
        points
      }
      author {
        members {
          handle
        }
      }
      programmingLanguage
      verdict
      timeConsumedMillis
      memoryConsumedBytes
    }
  }
}

query GetSimulatedStandings($contestId: Int!, $timestamp: Int!, $from: Int, $count: Int) {
  contestStandingsSimulated(contestId: $contestId, timestamp: $timestamp, from: $from, count: $count) {
    contest {
      id
      name
      durationSeconds
      startTimeSeconds
    }
    problems {
      index
      name
      points
    }
    rows {
      rank
      points
      penalty
      party {
        members {
          handle
        }
      }
      problemResults {
        points
        rejectedAttemptCount
        bestSubmissionTimeSeconds
      }
    }
  }
}

query GetSimulatedSubmissions($contestId: Int!, $timestamp: Int!, $count: Int) {
  contestSubmissionsSimulated(contestId: $contestId, timestamp: $timestamp, count: $count) {
    submissions {
      id
      creationTimeSeconds
      relativeTimeSeconds
      problem {
        index
        name
      }
      author {
        members {
          handle
        }
      }
      programmingLanguage
      verdict
    }
  }
}

query GetSimulationState($contestId: Int!, $currentTimestamp: Int!, $speedMultiplier: Float) {
  simulationState(contestId: $contestId, currentTimestamp: $currentTimestamp, speedMultiplier: $speedMultiplier) {
    contestId
    startTime
    currentTime
    speedMultiplier
    isRunning
    progress
  }
}

query GetCompleteContestData($contestId: Int!) {
  contest: contest(id: $contestId) {
    id
    name
    type
    phase
    frozen
    durationSeconds
    startTimeSeconds
    relativeTimeSeconds
  }
  problems: contestProblems(contestId: $contestId) {
    contestId
    index
    name
    type
    points
    rating
    tags
  }
  standings: contestStandings(contestId: $contestId, from: 1, count: 100) {
    rows {
      rank
      points
      penalty
      party {
        members {
          handle
        }
      }
    }
  }
  submissions: contestSubmissions(contestId: $contestId, count: 50) {
    submissions {
      id
      creationTimeSeconds
      problem {
        index
        name
      }
      author {
        members {
          handle
        }
      }
      verdict
    }
  }
}
